var simpleexpressControllers=angular.module("simpleexpressControllers",[]);simpleexpressControllers.controller("IndexController",["$scope","$http","Articles",function(e,t,l){e.articles=l.query(),e.article={},e.clearArticle=function(){e.article={}},e["delete"]=function(t){t&&l["delete"]({id:t},function(){e.articles=l.query(function(){$("#deletePrompt").modal("hide")})})},e.deletePrompt=function(t){e.article=t,$("#deletePrompt").modal("show")},e.save=function(t){t?console.log("Modify article. To be coded."):l.save(e.article,function(){e.articles=l.query(),e.article={}})},e.modifyPrompt=function(t){e.article=t,$("#addArticle").modal("show")},e.view=function(t){e.article=l.get({id:t},function(){$("#viewArticle").modal("show")})}}]);