var simpleexpressControllers=angular.module("simpleexpressControllers",[]);simpleexpressControllers.controller("IndexController",["$scope","$http","Articles",function(e,l,t){e.articles=t.query(),e.article={},e.clearArticle=function(){e.article={}},e["delete"]=function(l){e.article={},l&&t["delete"]({id:l},function(){e.articles=t.query(function(){$("#deletePrompt").modal("hide")})})},e.deletePrompt=function(l){e.article=l,$("#deletePrompt").modal("show")},e.save=function(l){l?(t.save({id:l,article:e.article},function(){e.article={},e.articles=t.query(),$("#addArticle").modal("hide")}),console.log("Modify article. To be coded.")):t.save(e.article,function(){e.articles=t.query(),e.article={},$("#addArticle").modal("hide")})},e.modifyPrompt=function(l){e.article=l,$("#addArticle").modal("show")},e.view=function(l){e.article=t.get({id:l},function(){$("#viewArticle").modal("show")})}}]);