var simpleexpressControllers=angular.module("simpleexpressControllers",[]);simpleexpressControllers.controller("IndexController",["$scope","$http","Articles",function(e,l,r){e.articles=r.query(),e.add=function(){r.save(e.article,function(){e.articles=r.query(),e.article={}})},e.view=function(l){e.article=r.get({id:l},function(){console.log(e.article),$("#viewArticle").modal("show")})}}]);